  --font-sans: 'Inter', system-ui, sans-serif;
  --font-heading: 'Poppins', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 0.75rem;

  /* DOMREALCE Brand Colors - Extra√≠das do log√≥tipo */
  --brand-yellow: hsl(50, 100%, 60%);
  /* Amarelo do "DOM" */
  --brand-red: hsl(0, 85%, 50%);
  /* Vermelho do "REALCE" */
  --brand-turquoise: hsl(180, 70%, 50%);
  /* Azul turquesa do c√≠rculo */
  --brand-green: hsl(120, 60%, 40%);
  /* Verde do c√≠rculo */
  --brand-dark-red: hsl(0, 70%, 30%);
  /* Vermelho escuro do c√≠rculo */
  --brand-dark: hsl(0, 0%, 0%);
  /* Preto para texto */
  --brand-white: hsl(0, 0%, 100%);
  /* Branco para fundos */

  /* Alias para manter compatibilidade com c√≥digo existente */
  --brand-coral: var(--brand-red);
  /* Coral = Vermelho */
  --brand-blue: var(--brand-turquoise);
  /* Azul = Turquesa */
}

/* Ajuste de cores para Toast (for√ßa texto preto) */
[data-sonner-toast] {
  background-color: #ffffff !important;
  border: 2px solid #000000 !important;
  color: #000000 !important;
}

[data-sonner-toast] [data-title],
[data-sonner-toast] [data-description] {
  color: #000000 !important;
}

.dark {
  --background: var(--brand-dark);
  --foreground: var(--brand-white);
  --card: hsl(0, 0%, 8%);
  --card-foreground: var(--brand-white);
  --popover: hsl(0, 0%, 8%);
  --popover-foreground: var(--brand-white);
  --primary: var(--brand-yellow);
  --primary-foreground: var(--brand-dark);
  --secondary: var(--brand-red);
  --secondary-foreground: var(--brand-white);
  --muted: hsl(0, 0%, 15%);
  --muted-foreground: hsl(0, 0%, 65%);
  --accent: var(--brand-turquoise);
  --accent-foreground: var(--brand-dark);
  --destructive: var(--brand-red);
  --destructive-foreground: var(--brand-white);
  --border: hsl(0, 0%, 20%);
  --input: hsl(0, 0%, 15%);
  --ring: var(--brand-yellow);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased;
    font-family: var(--font-sans);
    background: transparent;
    color: white;
  }

  h1,
