  }

  /* Gradientes usando as cores do log√≥tipo */
  .bg-gradient-logo {
    background: linear-gradient(135deg, var(--brand-yellow), var(--brand-red), var(--brand-turquoise));
  }

  .bg-gradient-logo-hover:hover {
    background: linear-gradient(135deg, var(--brand-red), var(--brand-turquoise), var(--brand-green));
    transition: background 0.3s ease;
  }
}

/* Advanced Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }

  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }

  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes float {

  0%,
  100% {
    transform: translateY(0px);
  }

  50% {
    transform: translateY(-20px);
  }
}

@keyframes float-slow {

  0%,
  100% {
    transform: translateY(0px) rotate(0deg);
  }

  50% {
    transform: translateY(-10px) rotate(3deg);
  }
}

@keyframes glow {

  0%,
  100% {
    box-shadow: 0 0 20px rgba(51, 217, 178, 0.3);
  }

  50% {
    box-shadow: 0 0 40px rgba(51, 217, 178, 0.6);
  }
}

@keyframes pulse-brand {

