/* 3D Transform Effects */
.transform-3d {
  transform-style: preserve-3d;
  transition: transform 0.3s ease;
}

.tilt-hover:hover {
  transform: perspective(1000px) rotateX(5deg) rotateY(5deg) scale(1.02);
}

/* Mouse tracking effect */
.mouse-track {
  transition: transform 0.1s ease-out;
}

/* Parallax effects */
.parallax-slow {
  transition: transform 0.3s ease-out;
}

.parallax-fast {
  transition: transform 0.2s ease-out;
}

/* Cursor visibility fixes */
* {
  cursor: inherit !important;
}

body {
  cursor: auto !important;
}

/* Cursor espec√≠fico para elementos interativos */
button,
a,
[role="button"],
[onClick],
.cursor-pointer {
  cursor: pointer !important;
}

/* Cursor de texto para inputs */
input[type="text"],
input[type="email"],
input[type="tel"],
textarea {
  cursor: text !important;
}

/* Garante cursor vis√≠vel em imagens clic√°veis */
img[data-clickable],
.hover-lift img,
.group img {
  cursor: pointer !important;
}

/* Hover states with enhanced effects */
.hover-lift {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.hover-lift:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.hover-tilt {
  transition: all 0.3s ease;
}

.hover-tilt:hover {
  transform: perspective(1000px) rotateX(2deg) rotateY(2deg) scale(1.02);
}

/* Intersection Observer animations - DISABLED to fix visibility */
.scroll-animate {
  opacity: 1;
  transform: translateY(0px);
  transition: all 0.6s ease-out;
}

.scroll-animate.in-view {
  opacity: 1;
  transform: translateY(0);
}

.scroll-animate-left {
  opacity: 1;
  transform: translateX(0px);
  transition: all 0.8s ease-out;
}

.scroll-animate-left.in-view {
  opacity: 1;
  transform: translateX(0);
}

.scroll-animate-right {
  opacity: 1;
  transform: translateX(0px);
  transition: all 0.8s ease-out;
}

.scroll-animate-right.in-view {
  opacity: 1;
  transform: translateX(0);
}

/* Gradient animation */
.gradient-animate {
  background-size: 400% 400%;
  animation: gradient-shift 4s ease infinite;
}

@keyframes gradient-shift {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

/* DOMREALCE: Performance & SEO Enhancements */

/* Smooth scrolling and performance optimizations */
html {
  scroll-behavior: smooth;
}

/* Font display optimization */
@font-face {
  font-family: 'Inter';
  font-display: swap;
}

@font-face {
  font-family: 'Poppins';
  font-display: swap;
}

/* Image rendering optimizations */
img {
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
}

/* Hero overlay utility classes */
.hero-overlay {
  position: relative;
  isolation: isolate;
}

.hero-overlay::after {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.2));
  z-index: -1;
}

.hero-overlay-light::after {
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.25), rgba(0, 0, 0, 0.1));
}

.hero-overlay-dark::after {
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.3));
}

/* Enhanced WhatsApp FAB animations */
@keyframes whatsapp-pulse {
  0%, 100% {
    box-shadow: 0 0 0 0 rgba(37, 211, 102, 0.4);
  }
  50% {
    box-shadow: 0 0 0 10px rgba(37, 211, 102, 0);
  }
}

/* DOMREALCE: Hero Slider Specific Styles */
.duration-600 {
  transition-duration: 600ms;
}

/* Hero slider image optimization */
.hero-slider-img {
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
}

/* Smooth fade transitions for slider */
.slider-fade {
  transition: opacity 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Controls hover effects */
.slider-control {
  backdrop-filter: blur(4px);
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.slider-control:hover {
  transform: scale(1.05);
}

/* DOMREALCE: Performance utils */
.below-the-fold {
  content-visibility: auto;
  contain-intrinsic-size: 1px 1000px;
}

/* DOMREALCE: Skip link for accessibility */
.skip-link {
  position: absolute;
  top: -40px;
  left: 6px;
  background: #000;
  color: #fff;
  padding: 8px;
  text-decoration: none;
  border-radius: 4px;
  z-index: 1000;
  transform: translateY(-100%);
  transition: transform 0.3s;
}

.skip-link:focus {
  transform: translateY(0%);
}

.whatsapp-pulse {
  animation: whatsapp-pulse 2s ease-in-out infinite;
}

/* Performance optimization for transforms */
.will-change-transform {
  will-change: transform;
}

.will-change-opacity {
  will-change: opacity;
}

/* Fade in animation for lazy loaded images */
.fade-in {
  opacity: 0;
  transition: opacity 0.3s ease-in-out;
}

.fade-in.loaded {
  opacity: 1;
}

/* Accessibility improvements */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  html {
    scroll-behavior: auto;
  }
}

