import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { toast } from '@/hooks/use-toast';

export default function TesteCores() {
  const [bgColor, setBgColor] = useState('#ffffff');
  const [textColor, setTextColor] = useState('#000000');
  const [borderColor, setBorderColor] = useState('#000000');
  const [customCSS, setCustomCSS] = useState('');

  const testarToast = () => {
    const toastStyle = {
      backgroundColor: bgColor,
      color: textColor,
      border: `2px solid ${borderColor}`,
      ...parseCustomCSS(customCSS)
    };

    toast({
      title: "‚úÖ Refer√™ncia Multibanco gerada",
      description: `üè¶ Entidade: 11249 | üî¢ Refer√™ncia: 123456789 | üí∞ Valor: ‚Ç¨43,05`,
      style: toastStyle,
      duration: 8000,
    });
  };

  const parseCustomCSS = (css: string) => {
    const styles: any = {};
    if (!css) return styles;

    const rules = css.split(';').filter(rule => rule.trim());
    rules.forEach(rule => {
      const [property, value] = rule.split(':').map(s => s.trim());
      if (property && value) {
        const camelCase = property.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
        styles[camelCase] = value;
      }
    });
    return styles;
  };

  const copiarCSS = () => {
    const cssCompleto = `
backgroundColor: '${bgColor}',
color: '${textColor}',
border: '2px solid ${borderColor}',
${customCSS ? Object.entries(parseCustomCSS(customCSS)).map(([k, v]) => `${k}: '${v}'`).join(',\n') : ''}
    `.trim();
    navigator.clipboard.writeText(cssCompleto);
    alert('CSS copiado!');
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-8 text-center">üé® Teste de Cores para Toasts</h1>

