import { ArrowLeft, Plus, Edit, Trash2, Save, X, FileText, Calendar, Tag } from "lucide-react";
import { Link } from "wouter";
import { useToast } from "@/hooks/use-toast";
import type { News } from "@shared/schema";

interface NewsForm {
  titulo: string;
  descricao: string;
  categoria: string;
  imagem: string;
  data: string;
}

export default function AdminNoticias() {
  const { toast } = useToast();
  const [noticias, setNoticias] = useState<News[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState<NewsForm>({
    titulo: "", descricao: "", categoria: "", imagem: "", data: ""
  });

  useEffect(() => {
    fetchNoticias();
  }, []);

  const fetchNoticias = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/admin/noticias');
      const data = await response.json();
      setNoticias(data.noticias || []);
    } catch (error) {
      console.error('Error fetching noticias:', error);
      toast({
        title: "Erro",
        description: "Falha ao carregar not√≠cias",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const saveNoticia = async (noticiaData: NewsForm, noticiaId?: string) => {
    try {
      const url = noticiaId ? `/api/admin/noticias/${noticiaId}` : '/api/admin/noticias';
      const method = noticiaId ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...noticiaData,
          data: new Date(noticiaData.data).toISOString()
        }),
      });

      if (response.ok) {
        await fetchNoticias();
        setEditingId(null);
        resetForm();
        toast({
          title: "Sucesso",
          description: noticiaId ? "Not√≠cia atualizada" : "Not√≠cia criada",
        });
      } else {
        throw new Error('Falha ao salvar not√≠cia');
      }
    } catch (error) {
      console.error('Error saving noticia:', error);
      toast({
        title: "Erro",
        description: "Falha ao salvar not√≠cia",
        variant: "destructive",
      });
    }
  };

  const deleteNoticia = async (noticiaId: string) => {
    if (!confirm('Tem certeza que deseja remover esta not√≠cia?')) return;

    try {
      const response = await fetch(`/api/admin/noticias/${noticiaId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchNoticias();
        toast({
          title: "Sucesso",
          description: "Not√≠cia removida",
        });
      } else {
        throw new Error('Falha ao remover not√≠cia');
      }
    } catch (error) {
      console.error('Error deleting noticia:', error);
      toast({
        title: "Erro",
        description: "Falha ao remover not√≠cia",
        variant: "destructive",
      });
    }
  };

  const resetForm = () => {
    setEditForm({
      titulo: "", descricao: "", categoria: "", imagem: "", data: ""
    });
  };

  const startEdit = (noticia: News) => {
    setEditingId(noticia.id);
    setEditForm({
      titulo: noticia.titulo,
      descricao: noticia.descricao,
      categoria: noticia.categoria,
      imagem: noticia.imagem,
      data: noticia.data ? new Date(noticia.data).toISOString().split('T')[0] : ""
    });
  };

  const cancelEdit = () => {
    setEditingId(null);
    resetForm();
  };

  const formatDate = (date: Date | null) => {
    if (!date) return 'N/A';
    return new Date(date).toLocaleDateString('pt-PT');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-[#0a0a0a] text-white">
        <Navigation />
        <div className="container mx-auto px-4 py-16 mt-16">
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-[#FFD700] mx-auto"></div>
            <p className="mt-4 text-gray-400">A carregar not√≠cias...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#0a0a0a] text-white">
      <Navigation />

      {/* Header */}
      <div className="bg-[#111111] border-b border-[#333] mt-16">
        <div className="container mx-auto px-4 py-8">
          <div className="flex items-center gap-4 mb-6">
            <Link href="/noticias">
              <Button variant="outline" size="sm" className="gap-2 border-[#FFD700] text-[#FFD700] hover:bg-[#FFD700] hover:text-black">
                <ArrowLeft className="h-4 w-4" />
                Voltar √†s Not√≠cias
              </Button>
            </Link>
          </div>
          <h1 className="text-4xl font-bold text-white mb-4">
            Administra√ß√£o de <span className="text-[#FFD700]">Not√≠cias</span>
          </h1>
          <p className="text-gray-300 text-lg">
            Gere as not√≠cias e artigos do site
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
