                />
              </div>
              <div className="md:col-span-2">
                <Label htmlFor="new-descricao" className="text-white">Descri√ß√£o</Label>
                <Textarea
                  id="new-descricao"
                  value={editForm.descricao}
                  onChange={(e) => setEditForm({ ...editForm, descricao: e.target.value })}
                  placeholder="Conte√∫do da not√≠cia"
                  rows={6}
                  className="bg-[#222] border-[#444] text-white mt-1"
                />
              </div>
              <div className="md:col-span-2 flex gap-2">
                <Button 
                  onClick={() => saveNoticia(editForm)}
                  className="bg-[#FFD700] text-black hover:bg-yellow-400"
                >
                  <Save className="w-4 h-4 mr-2" />
                  Salvar Not√≠cia
                </Button>
                <Button onClick={cancelEdit} variant="outline">
                  <X className="w-4 h-4 mr-2" />
                  Cancelar
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Add Button */}
        {editingId !== "new" && (
          <Card className="bg-[#111111] border-[#333] mb-8">
            <CardContent className="p-6 text-center">
              <Button 
                onClick={() => setEditingId("new")}
                className="bg-[#FFD700] text-black hover:bg-yellow-400"
              >
                <Plus className="w-4 h-4 mr-2" />
